#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\系统默认\自闭症儿童语音交互绘本项目描述\client\node_modules\lz-string\bin\node_modules;D:\系统默认\自闭症儿童语音交互绘本项目描述\client\node_modules\lz-string\node_modules;D:\系统默认\自闭症儿童语音交互绘本项目描述\client\node_modules;D:\系统默认\自闭症儿童语音交互绘本项目描述\node_modules;D:\系统默认\node_modules;D:\node_modules;D:\系统默认\自闭症儿童语音交互绘本项目描述\client\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/系统默认/自闭症儿童语音交互绘本项目描述/client/node_modules/lz-string/bin/node_modules:/mnt/d/系统默认/自闭症儿童语音交互绘本项目描述/client/node_modules/lz-string/node_modules:/mnt/d/系统默认/自闭症儿童语音交互绘本项目描述/client/node_modules:/mnt/d/系统默认/自闭症儿童语音交互绘本项目描述/node_modules:/mnt/d/系统默认/node_modules:/mnt/d/node_modules:/mnt/d/系统默认/自闭症儿童语音交互绘本项目描述/client/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../lz-string/bin/bin.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../lz-string/bin/bin.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../lz-string/bin/bin.js" $args
  } else {
    & "node$exe"  "$basedir/../lz-string/bin/bin.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
